// Combine all analysis results
const assignmentData = $input.all()[0].json;
const aiAnalysis = $input.all()[1].json;
const researchSuggestions = $input.all()[2].json;
const plagiarismAnalysis = $input.all()[3].json;

// Parse JSON responses from AI
const aiAnalysisParsed = typeof aiAnalysis === 'string' ? JSON.parse(aiAnalysis) : aiAnalysis;
const researchParsed = typeof researchSuggestions === 'string' ? JSON.parse(researchSuggestions) : researchSuggestions;
const plagiarismParsed = typeof plagiarismAnalysis === 'string' ? JSON.parse(plagiarismAnalysis) : plagiarismAnalysis;

const combinedResult = {
  assignment_id: assignmentData.assignment_id,
  student_id: assignmentData.student_id,
  analysis_timestamp: new Date().toISOString(),

  // From AI Analysis
  topic: aiAnalysisParsed.topic,
  key_themes: aiAnalysisParsed.key_themes,
  research_questions: aiAnalysisParsed.research_questions,
  academic_level: aiAnalysisParsed.academic_level,
  word_count: aiAnalysisParsed.word_count,
  key_concepts: aiAnalysisParsed.key_concepts,

  // From Research Suggestions
  suggested_sources: researchParsed.suggested_sources,
  citation_recommendations: researchParsed.citation_recommendations,

  // From Plagiarism Detection
  plagiarism_score: plagiarismParsed.plagiarism_score,
  confidence_score: plagiarismParsed.confidence_score,
  flagged_sections: plagiarismParsed.flagged_sections,
  overall_assessment: plagiarismParsed.overall_assessment,

  // Additional metadata
  processing_time: Date.now() - new Date(assignmentData.timestamp).getTime()
};

return [{
  json: combinedResult
}];

parameters.jsCode:
const results = $input.first().json;

// Prepare data for database insertion
const dbPayload = {
  assignment_id: results.assignment_id,
  suggested_sources: results.suggested_sources,
  plagiarism_score: results.plagiarism_score,
  flagged_sections: results.flagged_sections,
  research_suggestions: JSON.stringify({
    key_themes: results.key_themes,
    research_questions: results.research_questions,
    key_concepts: results.key_concepts
  }),
  citation_recommendations: JSON.stringify(results.citation_recommendations),
  confidence_score: results.confidence_score
};

// Update assignment with topic and academic level
const updateAssignment = {
  assignment_id: results.assignment_id,
  topic: results.topic,
  academic_level: results.academic_level,
  word_count: results.word_count
};

return [
  { json: { action: 'update_assignment', data: updateAssignment } },
  { json: { action: 'insert_analysis', data: dbPayload } }
];

